mapper.select:
select totalAmount,saleTime,orderStatus,preWinNum,userId,accWin,id as orderCode,issueCode,numberRecord from
hf_game_order where
lotteryId= #{lotteryId} and userId=#{userId}

    and orderStatus in (1,2,3)


    and orderStatus=#{status}

order by id desc limit 2000

mapper.update:
update hf_game_order set accWin=totalAmount*preWinNum,orderStatus=2,calculateWinTime=now()
where lotteryId= #{lotteryId} and issueCode=#{issueCode} and numberRecord in

    #{name}

update hf_game_order set orderStatus=3,calculateWinTime=now()
where lotteryId= #{lotteryId} and issueCode=#{issueCode} and numberRecord not in

    #{name}

mapper.select:
select userId, accWin, id
from hf_game_order
where orderStatus = 2
  and lotteryId = #{lotteryId}
  and issueCode = #{issueCode}
